import React, {Component} from 'react';
import chameleon_icon from './images/chameleon.svg'
import styles from './component-styles/App.scss'

class App extends Component { 
    constructor(props) {
        super(props)

        this.state = {
            canvas: null
        }
    }

componentDidMount() {
    //on mounting of component, create a variable = to DOM's canvas element
    var canvas = document.getElementById('canvas').getContext('2d');
    //update state
    this.setState({canvas}) 
}

drawToCanvas = (e) => {
    //On change of 'input' value, create a new image element
    var image = new Image()
    //Set src = to input's value
    image.src = e.target.value;
    image.onload.then()
}

//ES6 arrow function to bind to this

colorOfPixel = () => {
    // getting user coordinates
    this.setState({ x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });

    //Destructering 
    const {canvas, x, y} = this.state;

    // getting image data and RGB values
    var img_data = canvas.getImageData(x, y, 1, 1).data;
    var R = img_data[0]; var G = img_data[1]; var B = img_data[2];  
    document.getElementById("rgb").value = R + ',' + G + ',' + B;
    
    //Convert RGB values to Hex code
    function rgbToHex(R,G,B) {return toHex(R)+toHex(G)+toHex(B)}
    function toHex(n) {
    n = parseInt(n,10);
    if (isNaN(n)) return "00";
    n = Math.max(0,Math.min(n,255));
    return "0123456789ABCDEF".charAt((n-n%16)/16)  + "0123456789ABCDEF".charAt(n%16);
}
    var hex = rgbToHex(R, G, B);
    document.getElementById("hex").value = "#" + hex;
    document.getElementById("color-display").style.backgroundColor = "#" + hex;
}


render() {
   return(
        <div id="app_wrap">

            <img src={chameleon_icon}/>
            <div id="canvas_wrap"><canvas id="canvas"></canvas></div>
            <input onChange={e => this.drawToCanvas(e)}></input>
            
        </div>
      )
   } 
}

export default App;
