        }
    }

drawToCanvas = () => {
    var canvas = document.getElementById('canvas')
}


render() {
   return(
        <div id="app_wrap">

            <img src={chameleon_icon}/>
            <div id="canvas_wrap"><canvas id="canvas"></canvas></div>
            <input onChange={this.drawToCanvas}></input>
            
        </div>
      )
   } 
}

export default App;
